@WebServlet("/index.html")
public class login extends HttpServlet {
	private final String validUserName = "admin";
	private final String validPassWord = "root";
	private boolean checkParameter(String params) {
		if(params!=null && !params.trim().isEmpty() && !params.equals("null")) {
			return true;
		}else {
			return false;
		}
	}

	public void doGet(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, java.io.IOException {
        // Here we read the value of the submit button
        String action = request.getParameter("submit");
        String firstName = request.getParameter("firstname");
        String lastName = request.getParameter("lastname");
        String email = request.getParameter("email");
        String msg = request.getParameter("msg");

        boolean fNameCheck = checkParameter(firstName);
        boolean lNameCheck = checkParameter(lastName);
        /*
         * boolean emailCheck = (email != null && !email.trim().isEmpty() && email
         * .indexOf("@") != -1);
         */
        boolean emailCheck = checkParameter(email) && (email.indexOf("@") > 0);
        boolean msgCheck = checkParameter(msg);
        // Here we set the MIME type of the response, "text/html"
        response.setContentType("text/html");
        // Here we use a PrintWriter to send text data
        // to the client who has requested the servlet
        PrintWriter out = response.getWriter();

        // Here we start assembling the HTML content
        out.println("<!Doctype html><html><head>");
        out.println("<title>User Registeration Page</title>");
        out.println("<link rel='stylesheet' type='text/css' href='styles/styles.css'>");
        out.println("</head><body>");
        out.println("<div class='user_form'>");
        out.println("<h2>Please submit your information</h2>");
        // Here we set the value for method to post, so that
        // the servlet service method calls doPost in the
        // response to this form submit
        out.println("<form method='GET' + action='index.html'>");
        out.println("<table border><tr><th>");
        out.println("Your first name: </th><td ");
        if (action != null && !fNameCheck)
            out.println(" style='background-color:red;'");
        out.println("><input type='text' name='firstname' value='" + (firstName == null ? "" : firstName)
                + "' size='20'></td></tr>");
        out.println("<tr><th>Your last name: </th><td ");
        if (action != null && !lNameCheck)
            out.println(" style='background-color:red;'");
        out.println("><input type='text' name='lastname' value='" + (lastName == null ? "" : lastName)
                + "' size='20'></td></tr>");
        out.println("<tr><th>Your email: </th><td ");
        if (action != null && !emailCheck)
            out.println(" style='background-color:red;'");
        out.println(
                "><input type='text' name='email' value='" + (email == null ? "" : email) + "' size='20'></td></tr>");
        out.println("<tr><th>Your message: </th><td ");
        if (action != null && !msgCheck)
            out.println(" style='background-color:red;'");
        out.println("><textarea name='msg' rows='10' cols='30'>");
        if (msg != null)
            out.println(msg);
        out.println("</textarea></td></tr>");
        out.println("<tr><th></th><td><input type='submit' name='submit' value='Submit Info'></td></tr>");
        out.println("</table></form>");
        out.println("</div>");
        out.println("</body></html>");


        if (fNameCheck && lNameCheck && emailCheck && msgCheck)
            doPost(request, response);
    }


	public void doPost(HttpServletRequest request, HttpServletResponse response)
			   throws ServletException, java.io.IOException {

		  String userName=request.getParameter("username");
		  String password=request.getParameter("password");

		  String message= "";
		  PrintWriter out=response.getWriter();

		  GregorianCalendar calendar=new GregorianCalendar();


		  if(calendar.get(Calendar.AM_PM)==Calendar.AM) {
		   message="Good Morning";

		  }else {

		   message="Good Afternoon";

		  }



		  response.setContentType("text/html");


		  out.println("<html>");
		  out.println("<body>");

		  out.println("<a href='index.html'>Back</a>");
		  if(userName.equals(validUserName) && password.equals(validPassWord)) {
			     out.println("<p>" + message + ", " + userName + "</p>");
		  } else {
			    out.println("<p>" + message+ "! "+ userName + " not known! </p>");

		  } 


		  out.println("</body>");
		  out.println("</html>");
		  out.close();	






	}
}
